mapper.select:

from community c

where ctp.product_id = #{productId}
order by c.comm_date desc
limit #{startLimit}, #{sizeLimit}


from community c

left join product p on ctp.product_id = p.product_id
where p.product_cate_id = #{cateId}
group by commNo
limit #{startLimit}, #{sizeLimit}

from community c
left join product p on ctp.product_id = p.product_id left join product_cate pc on p.product_cate_id = pc.product_cate_id left join product_big_cate pbc on pc.product_big_cate_id = pbc.product_big_cate_id
pbc.product_big_cate_id IN
#{i}
group by commNo limit #{startLimit}, #{sizeLimit}

from community c
left join product p on ctp.product_id = p.product_id left join product_cate pc on p.product_cate_id = pc.product_cate_id join comm_tag ct on c.comm_no = ct.comm_no
ct.tag_id IN
#{i}
group by c.comm_no limit #{startLimit}, #{sizeLimit}


from community c
left join user_info u on c.user_id = u.user_id
where c.user_id = #{userId}
order by c.comm_date desc
limit #{startLimit}, #{sizeLimit}
